using System;
using System.Collections.Generic;
using System.Net.Http;
using System.Threading.Tasks;

namespace covid_rna
{
    public class Data
    {
        public const string VirusRna = 
            "";
        public const string VaccineRna =
            "";


        public static async Task<Dictionary<string ,string>> ReadAminoCodons()
        {
            var client = new HttpClient();
            var text = await client.GetStringAsync("https://raw.githubusercontent.com/berthubert/bnt162b2/master/codon-table-grouped.csv");
            var entries = text.Split(new char[] { '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries);
            Dictionary<string, string> dict = new();
            foreach(var item in entries)
            {
                var kv = item.Split(',');
                dict.Add(kv[1], kv[0]);
            }
            return dict;
        }

      

    }

    public class AminoAcid
    {
        public char Letter;
        public string Code; // 3 letter
        public string Name; 
    }

    public class Codon
    {
        public string Triplet;
        public AminoAcid AminoAcid;
    }
}
